import React from "react";
import "./Joke.css";


class Joke extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      votes: 0
    };
  }

  render() {
    const { votes } = this.state;
    const { id, joke, categories } = this.props;
    return (
      <div className="Joke">
        <div className="Joke-text">
          {joke}
          <div className="Joke-categories">
            {categories.map(category => (
              <span key={category} className="Joke-category">
                {category}
              </span>
            ))}
          </div>
        </div>
        <div className="Joke-votes">
          <button onClick={() => this.vote(1)}>
            <span role="img" aria-label="thumbs up">
              üëç
            </span>
          </button>
          <span className="Joke-voteCount">{votes}</span>
          <button onClick={() => this.vote(-1)}>
            <span role="img" aria-label="thumbs down">
              üëé
            </span>
          </button>
        </div>
      </div>
    );
  }

  vote(delta) {
    this.setState(prevState => ({
      votes: prevState.votes + delta
    }));
  }

}

